mapper.select:
select
    e.*


        ,s.salary AS current_salary

from employeeInfo e


    INNER JOIN (
        SELECT * FROM salaryList
    ) s ON e.id = s.user_id



        e.name like concat('%',#{name},'%')


        and e.gender = #{gender}


        and e.age >= #{startAge}


        and e.age <= #{endAge}


        and e.entry_date >= #{startJoindate}


        and e.res_date <= #{endJoindate}


        and e.pro_title = #{pro_title}


        and e.married_status = #{mar_status}


        and e.edu_bg = #{edu_bg}


        and e.dep = #{dep}


        and e.emp_status = #{emp_status}


        AND s.salary >= #{startSalary}


        AND s.salary <= #{endSalary}



    ORDER BY


            e.age
            e.joindate
            e.gender
            e.pro_title
            e.married_status
            e.edu_bg
            s.salary
            e.id 


            DESC
            ASC




    ORDER BY e.id ASC

mapper.update:
update employeeInfo
age = #{age}, dep = #{dep}, edu_bg = #{eduBg}, emp_status = #{empStatus}, entry_date = #{entryDate}, gender = #{gender}, id = #{id}, married_status = #{marStatus}, name = #{name}, pro_title = #{proTitle}, res_date = #{resDate}
where id = #{id}
